      onFilter: (value, record) => record.city.startsWith(value),
      filterSearch: true,
      width: "40%",
    },
    {
      title: "Country",
      dataIndex: "country",
      filters: [
        {
          text: props.data.country,
          value: props.data.country,
        },
        {
          text: "London",
          value: "London",
        },
        {
          text: "New York",
          value: "New York",
