import React from "react";
import {
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  CardBody,
  Card,
  Container,
  CardHeader,
  Button,
} from "reactstrap";

// node.js library that concatenates classes (strings)
import classnames from "classnames";

function OccupationalDetail(prevStep) {
  const [yourName, setyourName] = React.useState(false);
  const [emailAddress, setemailAddress] = React.useState(false);
  const [phoneNumber, setphoneNumber] = React.useState(false);
  const [firstName, setFirstName] = React.useState("");
  const [lastName, setLastName] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [phone, setPhone] = React.useState("");
  const [alternatePhone, setAlternatePhone] = React.useState("");
  const [birthPlace, setBirthPlace] = React.useState("");
  const [cast, setCast] = React.useState("");
  const [religion, setReligion] = React.useState("");
  const [motherTongue, setMotherTongue] = React.useState("");
  const [parmanentAddress, setParmanentAddress] = React.useState("");
  const [presentAddress, setPresentAddress] = React.useState("");

  const SubmitFormData = (e) => {
    e.preventDefault();
  };

  return (
    <>
      <div className="Add-Staff">
        <Container className="mt--20" fluid>
          <Card>
            <CardHeader>
              <h3 className="mb-5">Residential Details</h3>
            </CardHeader>
            <CardBody>
              <Form onSubmit={SubmitFormData}>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Contact Person Name"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setPresentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Relation"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setParmanentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
