import React from "react";

// javascipt plugin for creating charts
import { Chart } from "chart.js";

import "./Dashboard.css";

// import the stylesheet
import "react-step-progress/dist/index.css";

// import { chartOptions, parseOptions } from "variables/charts.js";
import StaffMember from "../staffMemeber/StaffMember";
import ResidentialDetail from "../ResidentialDetail/ResidentialDetail";
import ContactDetail from "../ContactDetail/ContactDetail";
// import OccupationalDetail from "../OccupationalDetail/OccupationalDetail";
import UserData from "../userDataTable/UserData";

// if (window.Chart) {
//   parseOptions(Chart, chartOptions());
// }

// import the progress bar
import StepProgressBar from "react-step-progress";
// import the stylesheet
import "react-step-progress/dist/index.css";

import { Steps, Button, message } from "antd";

const { Step } = Steps;

function Dashboard() {
  //state for steps
  const [step, setStep] = React.useState(1);

  // function for going to next step by increasing step state by 1
  const nextStep = () => {
    setStep(step + 1);
  };

  // function for going to previous step by decreasing step state by 1
  const prevStep = () => {
    setStep(step - 1);
  };

  //state for form data
  const [formData, setFormData] = React.useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    password: "",
    address: "",
    pin: "",
    city: "",
    country: "",
    age: "",
    alternateAddress: "",
  });
  console.log(formData);

  // handling form input data by taking onchange value and updating our previous form data state
  const handleInputData = (input) => (e) => {
    // input value from the form
    const { value } = e.target;

    //updating for data state taking previous state and then adding new value to create new object
    setFormData((prevState) => ({
      ...prevState,
      [input]: value,
    }));
  };

  // switch (step) {
  //   case 1:
  //     return (
  //       <StaffMember
  //         nextStep={nextStep}
  //         handleFormData={handleInputData}
  //         values={formData}
  //       />
  //     );
  //   case 2:
  //     return (
  //       <ResidentialDetail
  //         nextStep={nextStep}
  //         prevStep={prevStep}
  //         handleFormData={handleInputData}
  //         values={formData}
  //       />
  //     );
  //   case 3:
  //     return (
  //       <ContactDetail
  //         nextStep={nextStep}
  //         prevStep={prevStep}
  //         handleFormData={handleInputData}
  //         values={formData}
  //       />
  //     );

  //   case 4:
  //     return <UserData data={formData} />;

  //   default:
  //     break;
  // }

  const steps = [
    {
      title: "First",
      content: (
        <StaffMember
          nextStep={nextStep}
          handleFormData={handleInputData}
          values={formData}
        />
      ),
    },
    {
      title: "Second",
      content: (
        <ResidentialDetail
          nextStep={nextStep}
          prevStep={prevStep}
          handleFormData={handleInputData}
          values={formData}
        />
      ),
    },
    {
      title: "Last",
      content: (
        <ContactDetail
          nextStep={nextStep}
          prevStep={prevStep}
          handleFormData={handleInputData}
          values={formData}
        />
      ),
    },
  ];

  const [current, setCurrent] = React.useState(0);

  const next = () => {
    setCurrent(current + 1);
  };

  const prev = () => {
    setCurrent(current - 1);
  };

  return (
    <>
      <Steps current={current}>
        {steps.map((item) => (
          <Step key={item.title} title={item.title} />
        ))}
      </Steps>
      <div className="steps-content">{steps[current].content}</div>
      <div className="steps-action">
        {current < steps.length - 1 && (
          <Button type="primary" onClick={() => next()}>
            Next
          </Button>
        )}
        {current === steps.length - 1 && (
          <Button
            type="primary"
            onClick={() => message.success("Processing complete!")}
          >
            Done
          </Button>
        )}
        {current > 0 && (
          <Button style={{ margin: "0 8px" }} onClick={() => prev()}>
            Previous
          </Button>
        )}
      </div>
    </>
  );
}

export default Dashboard;
