import React from "react";
import {
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  CardBody,
  Card,
  Container,
  CardHeader,
} from "reactstrap";

// node.js library that concatenates classes (strings)
import classnames from "classnames";

function ResidentialDetail() {
  const [yourName, setyourName] = React.useState(false);
  const [zip, setzip] = React.useState("");
  if (zip === "") {
    setzipState("invalid");
  } else {
    setzipState("valid");
  }

  return (
    <>
      <div className="Add-Staff">
        <Container className="mt--20" fluid>
          <Card>
            <CardHeader>
              <h3 className="mb-5">Staff Member Details</h3>
            </CardHeader>
            <CardBody>
              <Form>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Present Address"
                          type="text"
                          onFocus={(e) => setyourName(true)}
                          onBlur={(e) => setyourName(false)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Parmanent Address"
                          type="text"
                          onFocus={(e) => setyourName(true)}
                          onBlur={(e) => setyourName(false)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="6">
                    <Input
                      id="validationCustom05"
                      placeholder="Zip"
                      type="text"
                      valid={zipState === "valid"}
                      invalid={zipState === "invalid"}
                      onChange={(e) => {
                        setzip(e.target.value);
                        if (e.target.value === "") {
                          setzipState("invalid");
                        } else {
                          setzipState("valid");
                        }
                      }}
                    />
                  </Col>
                </Row>
              </Form>
            </CardBody>
          </Card>
        </Container>
      </div>
    </>
  );
}

export default ResidentialDetail;
