import React from "react";
import {
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  CardBody,
  Card,
  Container,
  CardHeader,
  Button,
} from "reactstrap";

import validator from "validator";

// node.js library that concatenates classes (strings)
import classnames from "classnames";

function ResidentialDetail({ nextStep, handleFormData, prevStep, values }) {
  const [yourName, setyourName] = React.useState(false);
  const [presentAddress, setPresentAddress] = React.useState("");
  const [parmanentAddress, setParmanentAddress] = React.useState("");

  //creating error state for validation
  const [error, setError] = React.useState(false);

  // after form submit validating the form data using validator
  const submitFormData = (e) => {
    e.preventDefault();

    // checking if value of first name and last name is empty show error else take to next step
    if (
      validator.isEmpty(values.address) ||
      validator.isEmpty(values.pin) ||
      validator.isEmpty(values.city) ||
      validator.isEmpty(values.country)
    ) {
      setError(true);
    } else {
      nextStep();
    }
  };

  return (
    <>
      <div className="Add-Staff">
        <Container className="mt--20" fluid>
          <Card>
            <CardHeader>
              <h3 className="mb-5">Residential Details</h3>
            </CardHeader>
            <CardBody>
              <Form onSubmit={submitFormData}>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-home" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          style={{ border: error ? "2px solid red" : "" }}
                          name="address"
                          defaultValue={values.address}
                          type="text"
                          placeholder="Address"
                          onChange={handleFormData("address")}
                        />
                        <br />
                        {error ? (
                          <p style={{ color: "red" }}>
                            This is a required field
                          </p>
                        ) : (
                          ""
                        )}
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-code" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          style={{ border: error ? "2px solid red" : "" }}
                          name="pin"
                          defaultValue={values.pin}
                          type="text"
                          placeholder="pin"
                          onChange={handleFormData("pin")}
                        />
                        <br />
                        {error ? (
                          <p style={{ color: "red" }}>
                            This is a required field
                          </p>
                        ) : (
                          ""
                        )}
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-city" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          style={{ border: error ? "2px solid red" : "" }}
                          name="city"
                          defaultValue={values.city}
                          type="text"
                          placeholder="City"
                          onChange={handleFormData("city")}
                        />
                        <br />
                        {error ? (
                          <p style={{ color: "red" }}>
                            This is a required field
                          </p>
                        ) : (
                          ""
                        )}
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fa fa-globe" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          style={{ border: error ? "2px solid red" : "" }}
                          name="country"
                          defaultValue={values.country}
                          type="text"
                          placeholder="Country"
                          onChange={handleFormData("country")}
                        />
                        <br />
                        {error ? (
                          <p style={{ color: "red" }}>
                            This is a required field
                          </p>
                        ) : (
                          ""
                        )}
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Button color="primary" onClick={prevStep}>
                  Previous
                </Button>
                <Button color="primary" type="submit">
                  Continue
                </Button>
              </Form>
            </CardBody>
          </Card>
        </Container>
      </div>
    </>
  );
}

export default ResidentialDetail;
