import React from "react";
import {
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  CardBody,
  Card,
  Container,
  CardHeader,
  Button,
  Label,
} from "reactstrap";

// node.js library that concatenates classes (strings)
import classnames from "classnames";

import validator from "validator";

function StaffMember({ nextStep, handleFormData, values }) {
  const [yourName, setyourName] = React.useState(false);
  const [emailAddress, setemailAddress] = React.useState(false);
  const [phoneNumber, setphoneNumber] = React.useState(false);

  //creating error state for validation
  const [error, setError] = React.useState(false);

  // after form submit validating the form data using validator
  const submitFormData = (e) => {
    e.preventDefault();

    // checking if value of first name and last name is empty show error else take to step 2
    if (
      validator.isEmpty(values.firstName) ||
      validator.isEmpty(values.lastName)
    ) {
      setError(true);
    } else {
      nextStep();
    }
  };

  return (
    <>
      <div className="Add-Staff">
        <Container className="mt--20" fluid>
          <Card>
            <CardHeader>
              <h3 className="mb-5">Staff Member Details</h3>
            </CardHeader>
            <CardBody>
              <Form onSubmit={submitFormData}>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <Label
                        className="form-control-label"
                        htmlFor="example-email-input"
                        md="2"
                      >
                        Firstname
                      </Label>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          // placeholder="First name"
                          // type="text"
                          // onFocus={(e) => setyourName(true)}
                          // onBlur={(e) => setyourName(false)}
                          // required
                          style={{ border: error ? "2px solid red" : "" }}
                          name="firstName"
                          defaultValue={values.firstName}
                          type="text"
                          placeholder="First Name"
                          onChange={handleFormData("firstName")}
                        />
                        {error ? (
                          <p style={{ color: "red" }}>
                            This is a required field
                          </p>
                        ) : (
                          ""
                        )}
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <Label
                        className="form-control-label"
                        htmlFor="example-email-input"
                        md="2"
                      >
                        Lastname
                      </Label>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Last name"
                          type="text"
                          onFocus={(e) => setyourName(true)}
                          onBlur={(e) => setyourName(false)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>

                <Row>
                  <Col md="6">
                    <FormGroup>
                      <Label
                        className="form-control-label"
                        htmlFor="example-email-input"
                        md="2"
                      >
                        Email
                      </Label>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: emailAddress,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-envelope" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Email address"
                          type="email"
                          onFocus={(e) => setemailAddress(true)}
                          onBlur={(e) => setemailAddress(false)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <Label
                        className="form-control-label"
                        htmlFor="example-email-input"
                        md="2"
                      >
                        Phone
                      </Label>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: phoneNumber,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-globe-americas" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Phone number"
                          type="text"
                          onFocus={(e) => setphoneNumber(true)}
                          onBlur={(e) => setphoneNumber(false)}
                        />
                        <InputGroupAddon addonType="append">
                          <InputGroupText>
                            <i className="fas fa-phone" />
                          </InputGroupText>
                        </InputGroupAddon>
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Button color="primary" type="submit">
                  Continue
                </Button>
              </Form>
            </CardBody>
          </Card>
        </Container>
      </div>
    </>
  );
}

export default StaffMember;
