/*!

=========================================================
* Argon Dashboard PRO React - v1.2.1
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-pro-react
* Copyright 2021 Creative Tim (https://www.creative-tim.com)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
// node.js library that concatenates classes (strings)
import classnames from "classnames";
// javascipt plugin for creating charts
import { Chart } from "chart.js";
// react plugin used to create charts
import { Line, Bar } from "react-chartjs-2";
// reactstrap components
// import {
//   Badge,
//   Button,
//   Card,
//   CardHeader,
//   CardBody,
//   DropdownMenu,
//   DropdownItem,
//   DropdownToggle,
//   UncontrolledDropdown,
//   Form,
//   Input,
//   ListGroupItem,
//   ListGroup,
//   Media,
//   NavItem,
//   NavLink,
//   Nav,
//   Progress,
//   Table,
//   Container,
//   Row,
//   Col,
//   UncontrolledTooltip,
// } from "reactstrap";
import {
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  CardBody,
  Card,
  Container,
  CardHeader,
} from "reactstrap";

import "./Dashboard.css";

import { chartOptions, parseOptions } from "variables/charts.js";

function Dashboard() {
  const [yourName, setyourName] = React.useState(false);
  const [emailAddress, setemailAddress] = React.useState(false);
  const [location, setlocation] = React.useState(false);
  const [password, setpassword] = React.useState(false);
  const [paymentMethos, setpaymentMethos] = React.useState(false);
  const [phoneNumber, setphoneNumber] = React.useState(false);
  const [activeNav, setActiveNav] = React.useState(1);
  const [chartExample1Data, setChartExample1Data] = React.useState("data1");
  const toggleNavs = (e, index) => {
    e.preventDefault();
    setActiveNav(index);
    setChartExample1Data(chartExample1Data === "data1" ? "data2" : "data1");
  };
  if (window.Chart) {
    parseOptions(Chart, chartOptions());
  }
  return (
    <>
      <div className="Add-Staff">
        <Container className="mt--20" fluid>
          <Card>
            <CardHeader>
              <h3 className="mb-0">Staff Member Details</h3>
            </CardHeader>
            <CardBody>
              <Form>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="First name"
                          type="text"
                          onFocus={(e) => setyourName(true)}
                          onBlur={(e) => setyourName(false)}
                        />
                      </InputGroup>
                      <Input
                        placeholder="Last name"
                        type="text"
                        onFocus={(e) => setyourName(true)}
                        onBlur={(e) => setyourName(false)}
                      />
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: emailAddress,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-envelope" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Email address"
                          type="email"
                          onFocus={(e) => setemailAddress(true)}
                          onBlur={(e) => setemailAddress(false)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: location,
                        })}
                      >
                        <Input
                          placeholder="Location"
                          type="text"
                          onFocus={(e) => setlocation(true)}
                          onBlur={(e) => setlocation(false)}
                        />
                        <InputGroupAddon addonType="append">
                          <InputGroupText>
                            <i className="fas fa-map-marker" />
                          </InputGroupText>
                        </InputGroupAddon>
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: password,
                        })}
                      >
                        <Input
                          placeholder="Password"
                          type="password"
                          onFocus={(e) => setpassword(true)}
                          onBlur={(e) => setpassword(false)}
                        />
                        <InputGroupAddon addonType="append">
                          <InputGroupText>
                            <i className="fas fa-eye" />
                          </InputGroupText>
                        </InputGroupAddon>
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: paymentMethos,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-credit-card" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Payment method"
                          type="text"
                          onFocus={(e) => setpaymentMethos(true)}
                          onBlur={(e) => setpaymentMethos(false)}
                        />
                        <InputGroupAddon addonType="append">
                          <InputGroupText>
                            <small className="font-weight-bold">USD</small>
                          </InputGroupText>
                        </InputGroupAddon>
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: phoneNumber,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-globe-americas" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Phone number"
                          type="text"
                          onFocus={(e) => setphoneNumber(true)}
                          onBlur={(e) => setphoneNumber(false)}
                        />
                        <InputGroupAddon addonType="append">
                          <InputGroupText>
                            <i className="fas fa-phone" />
                          </InputGroupText>
                        </InputGroupAddon>
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
              </Form>
            </CardBody>
          </Card>
        </Container>
      </div>
    </>
  );
}

export default Dashboard;
