import React from "react";
import {
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  CardBody,
  Card,
  Container,
  CardHeader,
  Button,
} from "reactstrap";

// node.js library that concatenates classes (strings)
import classnames from "classnames";

function ContactDetail() {
  const [yourName, setyourName] = React.useState(false);
  const [presentAddress, setPresentAddress] = React.useState("");
  const [parmanentAddress, setParmanentAddress] = React.useState("");
  return (
    <>
      <div className="Add-Staff">
        <Container className="mt--20" fluid>
          <Card>
            <CardHeader>
              <h3 className="mb-5">Residential Details</h3>
            </CardHeader>
            <CardBody>
              <Form onSubmit={SubmitFormData}>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Contact Person Name"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setPresentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Relation"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setParmanentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Contact No."
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setParmanentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Address"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setParmanentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Pin Code"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setParmanentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="City"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setParmanentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="State"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setParmanentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Country"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setParmanentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Button type="submit">Continue</Button>
                <Button variant="primary" onClick={prevStep}>
                  Previous
                </Button>
              </Form>
            </CardBody>
          </Card>
        </Container>
      </div>
    </>
  );
}

export default ContactDetail;
