import React from "react";
import {
  // FormGroup,
  // Form,
  // Input,
  // InputGroupAddon,
  // InputGroupText,
  // InputGroup,
  // Row,
  // Col,
  CardBody,
  Card,
  Container,
  CardHeader,
  // Button,
} from "reactstrap";

import { Form, Input, InputNumber, Button } from "antd";

import validator from "validator";

// node.js library that concatenates classes (strings)
import classnames from "classnames";

function ContactDetail({ nextStep, handleFormData, prevStep, values }) {
  //creating error state for validation
  const [error, setError] = React.useState(false);

  // after form submit validating the form data using validator
  const submitFormData = (e) => {
    e.preventDefault();

    // checking if value of first name and last name is empty show error else take to next step
    if (
      validator.isEmpty(values.age) ||
      validator.isEmpty(values.alternateAddress)
    ) {
      setError(true);
    } else {
      nextStep(values);
    }
  };

  const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };

  /* eslint-disable no-template-curly-in-string */

  const validateMessages = {
    required: "${label} is required!",
    types: {
      email: "${label} is not a valid email!",
      number: "${label} is not a valid number!",
    },
    number: {
      range: "${label} must be between ${min} and ${max}",
    },
  };

  const onFinish = (values) => {
    console.log(values);
  };

  return (
    <>
      <div className="Add-Staff">
        {/* <Form
          {...layout}
          name="nest-messages"
          onFinish={onFinish}
          validateMessages={validateMessages}
        >
          <Form.Item
            name={["user", "name"]}
            label="Name"
            rules={[
              {
                required: true,
              },
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name={["user", "email"]}
            label="Email"
            rules={[
              {
                type: "email",
              },
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name={["user", "age"]}
            label="Age"
            rules={[
              {
                type: "number",
                min: 0,
                max: 99,
              },
            ]}
          >
            <InputNumber />
          </Form.Item>
          <Form.Item name={["user", "website"]} label="Website">
            <Input />
          </Form.Item>
          <Form.Item name={["user", "introduction"]} label="Introduction">
            <Input.TextArea />
          </Form.Item>
          <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Form.Item>
        </Form> */}
        <Container className="mt--20" fluid>
          <Card>
            <CardHeader>
              <h3 className="mb-5">Details</h3>
            </CardHeader>
            <CardBody>
              <Form
                {...layout}
                name="nest-messages"
                onFinish={onFinish}
                validateMessages={validateMessages}
              >
                <Row>
                  <Col>
                    <Form.Item
                      name={["user", "age"]}
                      label="Age"
                      rules={[
                        {
                          type: "number",
                          min: 0,
                          max: 99,
                        },
                      ]}
                    >
                      <InputNumber />
                    </Form.Item>
                  </Col>
                  <Col></Col>
                </Row>
                <Button type="primary" htmlType="submit">
                  Submit
                </Button>
              </Form>

              {/* <Form onSubmit={submitFormData}>
                <Row>
                  <Col>
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {})}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-home" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          style={{ border: error ? "2px solid red" : "" }}
                          
                          name="age"
                          defaultValue={values.age}
                          type="text"
                          placeholder="Age"
                          onChange={handleFormData("age")}
                        />
                        <br />
                        {error ? (
                          <p style={{ color: "red" }}>
                            This is a required field
                          </p>
                        ) : (
                          ""
                        )}
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {})}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-code" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          style={{ border: error ? "2px solid red" : "" }}
                          name="alternateAddress"
                          defaultValue={values.alternateAddress}
                          type="text"
                          placeholder="alternateAddress"
                          onChange={handleFormData("alternateAddress")}
                        />
                        <br />
                        {error ? (
                          <p style={{ color: "red" }}>
                            This is a required field
                          </p>
                        ) : (
                          ""
                        )}
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Button color="primary" onClick={prevStep}>
                  Previous
                </Button>
                <Button color="primary" type="submit">
                  Submit
                </Button>
              </Form> */}
            </CardBody>
          </Card>
        </Container>
      </div>
    </>
  );
}

export default ContactDetail;
