import React from "react";
import {
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Row,
  Col,
  CardBody,
  Card,
  Container,
  CardHeader,
  Button,
} from "reactstrap";

// node.js library that concatenates classes (strings)
import classnames from "classnames";

function ResidentialDetail({ nextStep, prevPage }) {
  const [yourName, setyourName] = React.useState(false);
  //   const [zip, setzip] = React.useState("");
  //   const [zipState, setzipState] = React.useState(null);
  const [presentAddress, setPresentAddress] = React.useState("");
  const [parmanentAddress, setParmanentAddress] = React.useState("");

  const SubmitFormData = (e) => {
    e.preventDefault();
    nextStep();
  };

  const previousPage = () => {
    prevPage();
  };

  //   if (zip === "") {
  //     setzipState("invalid");
  //   } else {
  //     setzipState("valid");
  //   }

  return (
    <>
      <div className="Add-Staff">
        <Container className="mt--20" fluid>
          <Card>
            <CardHeader>
              <h3 className="mb-5">Staff Member Details</h3>
            </CardHeader>
            <CardBody>
              <Form onSubmit={SubmitFormData}>
                <Row>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Present Address"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setPresentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                  <Col md="6">
                    <FormGroup>
                      <InputGroup
                        className={classnames("input-group-merge", {
                          focused: yourName,
                        })}
                      >
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="fas fa-user" />
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input
                          placeholder="Parmanent Address"
                          type="text"
                          //   onFocus={(e) => setyourName(true)}
                          //   onBlur={(e) => setyourName(false)}
                          onClick={(e) => setParmanentAddress(e.target.value)}
                        />
                      </InputGroup>
                    </FormGroup>
                  </Col>
                </Row>
                <Row>
                  {/* <Col md="6">
                    <Input
                      id="validationCustom05"
                      placeholder="Zip"
                      type="text"
                      valid={zipState === "valid"}
                      invalid={zipState === "invalid"}
                      onChange={(e) => {
                        setzip(e.target.value);
                        if (e.target.value === "") {
                          setzipState("invalid");
                        } else {
                          setzipState("valid");
                        }
                      }}
                    />
                  </Col> */}
                </Row>
                <Button type="submit">Continue</Button>
                <Button onClick={previousPage}>PrevPage</Button>
              </Form>
            </CardBody>
          </Card>
        </Container>
      </div>
    </>
  );
}

export default ResidentialDetail;
