import React from "react";
import { Table, Popconfirm } from "antd";
import "antd/dist/antd.css";

import { Modal, ModalBody, ModalHeader } from "reactstrap";
import Dashboard from "../dashboards/Dashboard";

function UserData(props) {
  console.log("props", props);

  const [modal, setModal] = React.useState(false);
  const toggle = () => setModal(!modal);

  const columns = [
    {
      title: "Name",
      dataIndex: "name",
      filters: [
        {
          text: props.data.firstName,
          value: props.data.firstName,
        },
        {
          text: "Joe",
          value: "Joe",
        },
        {
          text: "Category 1",
          value: "Category 1",
          children: [
            {
              text: "Yellow",
              value: "Yellow",
            },
            {
              text: "Pink",
              value: "Pink",
            },
          ],
        },
        {
          text: "Category 2",
          value: "Category 2",
          children: [
            {
              text: "Green",
              value: "Green",
            },
            {
              text: "Black",
              value: "Black",
            },
          ],
        },
      ],
      filterMode: "tree",
      filterSearch: true,
      onFilter: (value, record) => record.name.includes(value),
      width: "30%",
    },
    {
      title: "Age",
      dataIndex: "age",
      sorter: (a, b) => a.age - b.age,
    },
    {
      title: "Address",
      dataIndex: "address",
      filters: [
        {
          text: props.data.address,
          value: props.data.address,
        },
        {
          text: "London",
          value: "London",
        },
        {
          text: "New York",
          value: "New York",
        },
      ],
      onFilter: (value, record) => record.address.startsWith(value),
      filterSearch: true,
      width: "40%",
    },

    {
      title: "Phone",
      dataIndex: "phone",
      filters: [
        {
          text: props.data.phone,
          value: props.data.phone,
        },
        {
          text: "London",
          value: "London",
        },
        {
          text: "New York",
          value: "New York",
        },
      ],
      onFilter: (value, record) => record.phone.startsWith(value),
      filterSearch: true,
      width: "40%",
    },
    {
      title: "City",
      dataIndex: "city",
      filters: [
        {
          text: props.data.city,
          value: props.data.city,
        },
        {
          text: "London",
          value: "London",
        },
        {
          text: "New York",
          value: "New York",
        },
      ],
      onFilter: (value, record) => record.city.startsWith(value),
      filterSearch: true,
      width: "40%",
    },
    {
      title: "Country",
      dataIndex: "country",
      filters: [
        {
          text: props.data.country,
          value: props.data.country,
        },
        {
          text: "London",
          value: "London",
        },
        {
          text: "New York",
          value: "New York",
        },
      ],
      onFilter: (value, record) => record.country.startsWith(value),
      filterSearch: true,
      width: "40%",
    },
    {
      title: "alternateAddress",
      dataIndex: "alternateAddress",
      filters: [
        {
          text: props.data.alternateAddress,
          value: props.data.alternateAddress,
        },
        {
          text: "London",
          value: "London",
        },
        {
          text: "New York",
          value: "New York",
        },
      ],
      onFilter: (value, record) => record.alternateAddress.startsWith(value),
      filterSearch: true,
      width: "40%",
    },

    {
      key: "5",
      title: "Action",
      dataIndex: "",
      key: "x",
      render: () => <a onClick={toggle}>Edit</a>,
    },
    {
      title: "operation",
      dataIndex: "operation",
      render: (_, record) =>
        this.state.dataSource.length >= 1 ? (
          <Popconfirm
            title="Sure to delete?"
            onConfirm={() => handleDelete(record.key)}
          >
            <a>Delete</a>
          </Popconfirm>
        ) : null,
    },
  ];

  const data = [
    {
      key: "1",
      name: props.data.firstName,
      age: props.data.age,
      address: props.data.address,
      phone: props.data.phone,
      city: props.data.city,
      country: props.data.country,
      alternateAddress: props.data.alternateAddress,
    },
    {
      key: "2",
      name: "Jim Green",
      age: 42,
      address: "London No. 1 Lake Park",
    },
    {
      key: "3",
      name: "Joe Black",
      age: 32,
      address: "Sidney No. 1 Lake Park",
    },
    {
      key: "4",
      name: "Jim Red",
      age: 32,
      address: "London No. 2 Lake Park",
    },
  ];

  handleDelete = (key) => {
    const dataSource = [...this.state.dataSource];
    this.setState({
      dataSource: dataSource.filter((item) => item.key !== key),
    });
  };

  function onChange(pagination, filters, sorter, extra) {
    console.log("params", pagination, filters, sorter, extra);
  }

  return (
    <div>
      <Table columns={columns} dataSource={data} onChange={onChange} />
      <div>
        <Modal isOpen={modal} toggle={toggle}>
          <ModalHeader isClose={modal} toggle={toggle}>
            Update
          </ModalHeader>
          <ModalBody>
            <Dashboard />
          </ModalBody>
        </Modal>
      </div>
    </div>
  );
}

export default UserData;
