import React from "react";

// javascipt plugin for creating charts
import { Chart } from "chart.js";

import "./Dashboard.css";

import StepProgressBar from "react-step-progress";
// import the stylesheet
import "react-step-progress/dist/index.css";

import { chartOptions, parseOptions } from "variables/charts.js";
import StaffMember from "../staffMemeber/StaffMember";
import ResidentialDetail from "../ResidentialDetail/ResidentialDetail";
import ContactDetail from "../ContactDetail/ContactDetail";
import OccupationalDetail from "../OccupationalDetail/OccupationalDetail";

function Dashboard() {
  const [step, setStep] = React.useState(1);
  const step3Content = <h1></h1>;

  // setup step validators, will be called before proceeding to the next step
  function step2Validator() {
    return true;
  }

  function step3Validator() {
    // return a boolean
  }

  // function for going to next step by increasing step state by 1
  const nextStep = () => {
    setStep(step + 1);
  };

  // function for going to previous step by decreasing step state by 1
  const prevStep = () => {
    setStep(step - 1);
  };

  if (window.Chart) {
    parseOptions(Chart, chartOptions());
  }

  // switch (step) {
  //   case 1:
  //     return <StaffMember nextStep={nextStep} />;

  //   case 2:
  //     return <ResidentialDetail nextStep={nextStep} prevStep={prevStep} />;

  //   case 3:
  //     return <ContactDetail nextStep={nextStep} prevStep={prevStep} />;

  //   default:
  //     break;
  // }
  return (
    <div className="App">
      <StepProgressBar
        startingStep={0}
        steps={[
          {
            label: "Add Staff Member",
            name: "Add Staff Member",
            content: <StaffMember nextStep={nextStep} />,
          },
          {
            label: "Image-Acquisition",
            name: "Image-Acquisition",
            content: (
              <ResidentialDetail nextStep={nextStep} prevStep={prevStep} />
            ),
          },
          {
            label: "Image-processing",
            name: "Image Processing",
            content: <ContactDetail nextStep={nextStep} prevStep={prevStep} />,
            validator: step2Validator,
          },
          {
            label: "Finish",
            name: "Finish",
            content: <OccupationalDetail />,
          },
        ]}
      />
    </div>
  );
}

export default Dashboard;
