  //       <ResidentialDetail
  //         nextStep={nextStep}
  //         prevStep={prevStep}
  //         handleFormData={handleInputData}
  //         values={formData}
  //       />
  //     );
  //   case 3:
  //     return (
  //       <ContactDetail
  //         nextStep={nextStep}
  //         prevStep={prevStep}
  //         handleFormData={handleInputData}
  //         values={formData}
  //       />
  //     );

  //   case 4:
  //     return <UserData data={formData} />;

  //   default:
  //     break;
  // }

  // render the progress bar
  <StepProgressBar
    startingStep={0}
    onSubmit={onFormSubmit}
    steps={[
      {
        label: "Step 1",
        subtitle: "10%",
        name: "step 1",
        content: (
          <StaffMember
            nextStep={nextStep}
            handleFormData={handleInputData}
            values={formData}
          />
        ),
      },
      {
        label: "Step 2",
        subtitle: "50%",
        name: "step 2",
        content: (
          <ResidentialDetail
            nextStep={nextStep}
            prevStep={prevStep}
            handleFormData={handleInputData}
            values={formData}
          />
        ),
        validator: step2Validator,
      },
      {
        label: "Step 3",
        subtitle: "100%",
        name: "step 3",
        content: (
          <ContactDetail
            nextStep={nextStep}
            prevStep={prevStep}
            handleFormData={handleInputData}
            values={formData}
          />
        ),
        validator: step3Validator,
      },
    ]}
  />;
}

export default Dashboard;
